import { useState } from 'react';
import './App.css';

function App() {
    const [value, setValue] = useState(0);
    const [listValue, setList] = useState([]);

    const outputArray = (value) => {
        const newList = [];
        for (let i = 1; i <= value; i++) {
            if (i % 3 === 0 && i % 5 === 0) {
                newList.push('FizzBuzz');
            } else if (i % 3 === 0) {
                newList.push('Buzz');
            } else if (i % 5 === 0) {
                newList.push('Fizz');
            } else {
                newList.push(String(i));
            }
        }
        setList(newList);
    };

    return (
        <div className="App">
            <input 
                type="number"
                value={value}
                onChange={(e) => setValue(Number(e.target.value))}
                placeholder="Enter a number"
            />
            <button onClick={() => outputArray(value)}>Submit</button>

            {/* Displaying the list */}
            <div>
                {listValue.map((item, index) => (
                    <p key={index}>{item}</p>
                ))}
            </div>
        </div>
    );
}

export default App;
