import { useState } from 'react';
import './App.css';

function App() {
  const [value,setValue]=useState(0)
  const [clist,setList]=useState([])
  let list=[]
  const outputArray=(value)=>{
    for(let i=1;i<=value;i++){
      if(i%5===0 && i%3===0){
        setList([[...clist],"FizzBuzz"])
        alert(JSON.stringify(clist))
        // list[i-1]=("FizzBuzz")
        // alert(i)
      }
      else if(i%5===0 ){
        setList([[...clist],"Fizz"])
        alert(JSON.stringify(clist))
        // list[i-1]=("Fizz")
        // alert(i)
      }
      else if( i%3===0){
        setList([[...clist],"Buzz"])
        alert(JSON.stringify(clist))
        // list[i-1]=("Buzz")
        // alert(i)
      }
      else{
        setList([[...clist],i])
        alert(JSON.stringify(clist))
        // list[i-1]=(String(i))
        alert(String(i))
      }
    }
    // setList(list)
    console.log(list)
    list.map=(each)=>{
      // <h1>{each}</h1>
      console.log(each)
    }
  }

  return (
    <div className="App">
      <input onKeyUp={(e)=>setValue((e.target.value))}/>
      <input type='submit' value='submit' onClick={()=>outputArray(value)}/>
      {value}
      {clist}
      {JSON.stringify(list)}
    </div>
  );
}

export default App;
