import './App.css';
import 'bootstrap/dist/css/bootstrap.min.css';
import { useState } from 'react';
import Table from 'react-bootstrap/Table';
import Button from 'react-bootstrap/Button'

function App() {
  let [Todo,Changetodo]=useState( ["charger", "power bank", "bedsheet", "mobile phone"])
  let [inputvalue,changeinputvalue]=useState("")
  let [newtodo,deletetodo]=useState([])
  return (
    <div className="App ">
      <h1>TODOLIST</h1>
      <Table striped bordered hover variant="primary">
        <tr>
          <th>Order</th>
          <th>Value</th>
          <th>Delete</th>
        </tr>
        {Todo.map((t, i) => <tr>
          <td>{i + 1}</td>
          <td>{t}</td>
          <td><Button onClick={(e)=>{
            return(newtodo.map((m)=>{m!==e.target.value?deletetodo(m):false
            })
            )
          }}>Delete</Button></td>
        </tr>)}
      </Table>
      <form>
        <input type="test" onKeyUp={(e)=>changeinputvalue(e.target.value)}/>
        <button type="button" onClick={()=>Changetodo([...Todo,inputvalue])} >Add</button>
      </form>


    </div>
  );
}
export default App;
